import '../css/Deletemodal.css';
import React from "react";
import { useRef, useEffect } from 'react';

function Deletemodal({ setDeletemodalOpen, setMenu, menu_list, enteredNum, setEnterdNum, enteredName, setEnterdName, enteredURL, setEnterdURL, selectMenu, setSelect, comma}) {

  const closeDeletemodal = () => {
    setDeletemodalOpen(false);
  }
  const changeEnteredNum = (e) => {
    const value = e.target.value;
    const numCheck = /^[0-9,]/.test(value);
    if (!numCheck && value) {
      alert("가격에는 숫자만 입력해야합니다.")
      setEnterdNum("");
    };
    if (numCheck) {
      const removedCommaValue = Number(value.replaceAll(",", ""));
      setEnterdNum(removedCommaValue.toLocaleString());
    }
  };

  const changeEnteredName = (e) => {
    const value = e.target.value;
    setEnterdName(value);
  }

  const changeEnteredURL = (e) => {
    const value = e.target.value;
    setEnterdURL(value);
  }  

  const checkName = menu_list.findIndex(e => e.name === enteredName)

  const DeleteMenu = () => {
    const name = enteredName
    const price = enteredNum
    const image = enteredURL
  
    if (name === '' || price === '') {
      alert("이름과 가격은 필수 입력 사항입니다.")
    }
    else if (checkName !== -1 && selectMenu.name !== enteredName){
      alert("중복된 이름은 입력할 수 없습니다.")
      setEnterdName("");
    }
    else {
      const findIndex = menu_list.findIndex(e => e.id === selectMenu.id)
      console.log(findIndex)
      const new_menu_list = [...menu_list]
      if(findIndex !== -1){
        new_menu_list[findIndex] = {id: selectMenu.id, name: name, price: price, image: image}
      }
      setMenu(new_menu_list);
      setSelect(new_menu_list[findIndex])
      setEnterdName("");
      setEnterdNum("");
      setEnterdURL("");
      setDeletemodalOpen(false);
    }
  }

  const DeletemodalRef = useRef();
  useEffect(() => {
    const handler = (e) => {
      if (DeletemodalRef.current && !DeletemodalRef.current.contains(e.target)) {
        setDeletemodalOpen(false);
      }
    };

    document.addEventListener('mousedown', handler);

    return () => {
      document.removeEventListener('mousedown', handler);
    };
  });


  return (
    <div className='background'>
      <div ref={DeletemodalRef} className='container'>
        <b className='title'>메뉴 삭제</b>
        <p className='line'></p>
        <div className='buttonArea'>
          <button className='delete' onClick={()=>{DeleteMenu()}}>
            삭제
          </button>
          <button className='close' onClick={closeDeletemodal}>
            취소
          </button>
        </div>
      </div>
    </div>
  );
}

export default Deletemodal;
