import { useState } from 'react';
import '../css/MenuBox.css';
import '../css/Addmodal.css';
import Addmodal from './Addmodal.js';
import Updatemodal from './Updatemodal.js';
import Deletemodal from './Deletemodal';
import logo from '../waffle_logo.svg';
import editicon from '../edit.png';
import deleteicon from '../delete.svg';

function MenuBox({search}) {
  const [maxId, setMaxId] = useState(5);
  const [menu_list, setMenu] = useState([{
    "id": 1,
    "name": "초코와플",
    "price": 7000,
    "image": ""
  },
  {
    "id": 2,
    "name": "아메리카노",
    "price": 4000,
    "image": ""
  },
  {
    "id": 3,
    "name": "블루베리스무디",
    "price": 6000,
    "image": "https://upload.wikimedia.org/wikipedia/commons/1/15/Blueberries.jpg"
  },
  {
    "id": 4,
    "name": "딸기와플",
    "price": 7000,
    "image": "https://upload.wikimedia.org/wikipedia/commons/2/29/PerfectStrawberry.jpg"
  }]);

  const [enteredNum, setEnterdNum] = useState("");
  const [enteredName, setEnterdName] = useState("");
  const [enteredURL, setEnterdURL] = useState("");
  const [selectMenu, setSelect] = useState("");
  const select = (idx) => {
    setSelect(idx)
  }

  const comma = (str) => {
    str = String(str);
    return str.replace(/(\d)(?=(?:\d{3})+(?!\d))/g, '$1,');
  }

  const Menu = (search === null || search === '') ?
    menu_list.map((list) => (
    <div id={list.id} className={"Menu" + (list.id === selectMenu.id ? "select" : "")} onClick={() => { select(list) }}>
      <span id='selectid' className='MenuID'>{list.id}</span>
      <span className='MenuName'>{list.name}</span>
      <span className='MenuPrice'>{comma(list.price)}</span>
    </div>
  )) : menu_list.filter((el) => el.name.toString().toLowerCase().indexOf(search.toString().toLowerCase())>-1).map((list) => (
    <div id={list.id} className={"Menu" + (list.id === selectMenu.id ? "select" : "")} onClick={() => { select(list) }}>
      <span id='selectid' className='MenuID'>{list.id}</span>
      <span className='MenuName'>{list.name}</span>
      <span className='MenuPrice'>{comma(list.price)}</span>
    </div>
  ))

  const [AddmodalOpen, setAddmodalOpen] = useState(false);
  const showAddmodal = () => {
    setAddmodalOpen(true);
    setEnterdName("");
    setEnterdNum("");
    setEnterdURL("");
  };

  const [UpdatemodalOpen, setUpdatemodalOpen] = useState(false);
  const showUpdatemodal = () => {
    setUpdatemodalOpen(true);
    setEnterdName(selectMenu.name)
    setEnterdNum(selectMenu.price)
    setEnterdURL(selectMenu.image)
  };

  const [DeletemodalOpen, setDeletemodalOpen] = useState(false);
  const showDeletemodal = () => {
    setDeletemodalOpen(true);
    setEnterdName(selectMenu.name)
    setEnterdNum(selectMenu.price)
    setEnterdURL(selectMenu.image)
  };

  const handleError = (e) => {
    e.target.src = logo;
  }

  return (
    <div className="MenuBox">
      <div className='MenuBox_1'>
        <div className='MenuListBox'>
          <div className='MenuContent'>
            <div className='ID'><b>ID</b></div>
            <div className='Name'><b>이름</b></div>
            <div className='Price'><b>가격</b></div>
          </div>
          {Menu}
          <button id='AddButton' onClick={showAddmodal}>+</button>
        </div>
      </div>

      {selectMenu !== "" && 
      <div className='SelectBox'>
        <div className='MenuBox_2'>
          <button className='selectcancle' onClick={() => { setSelect("") }}>x</button>
          <img className='selectimage' src={selectMenu.image} alt={logo} onError={handleError} />
          <p className='selectname'>{selectMenu.name}</p>
          <p className='selectprice'>{comma(selectMenu.price)}원</p>
          <div className='editdelete'>
            <img className='editicon' src={editicon} onClick={showUpdatemodal} alt={logo} />
            <img className='deleteicon' src={deleteicon} onClick={showDeletemodal} alt={logo} />
          </div>
        </div>
      </div>}
      <Addmodal
        AddmodalOpen={AddmodalOpen} setAddmodalOpen={setAddmodalOpen} menu_list={menu_list} 
        setMenu={setMenu} maxId={maxId} setMaxId={setMaxId} enteredNum={enteredNum}
        setEnterdNum={setEnterdNum} enteredName={enteredName} setEnterdName={setEnterdName}
        enteredURL={enteredURL} setEnterdURL={setEnterdURL} setSelect={setSelect} />
      
      <Updatemodal
        UpdatemodalOpen={UpdatemodalOpen} setUpdatemodalOpen={setUpdatemodalOpen}
        menu_list={menu_list} setMenu={setMenu} maxId={maxId} setMaxId={setMaxId}
        enteredNum={enteredNum} setEnterdNum={setEnterdNum} enteredName={enteredName}
        setEnterdName={setEnterdName} enteredURL={enteredURL} setEnterdURL={setEnterdURL}
        selectMenu={selectMenu} setSelect={setSelect} comma={comma} />
    
      <Deletemodal DeletemodalOpen={DeletemodalOpen} setDeletemodalOpen={setDeletemodalOpen} 
        menu_list={menu_list} setMenu={setMenu} maxId={maxId} setMaxId={setMaxId}
        enteredNum={enteredNum} setEnterdNum={setEnterdNum} enteredName={enteredName}
        setEnterdName={setEnterdName} enteredURL={enteredURL} setEnterdURL={setEnterdURL}
        selectMenu={selectMenu} setSelect={setSelect}
      />
    
    </div>

  );
}

export default MenuBox;
