import { useState } from 'react';
import '../css/MenuBox.css';
import '../css/Addmodal.css';
import Addmodal from './Addmodal.js';
import logo from '../logo.svg'



function MenuBox() {
  const [maxId, setMaxId] = useState(5);
  const [menu_list, setMenu] = useState([{
    "id": 1,
    "name": "초코와플",
    "price": 7000,
    "image": ""
  },
  {
    "id": 2,
    "name": "아메리카노",
    "price": 4000,
    "image": ""
  },
  {
    "id": 3,
    "name": "블루베리스무디",
    "price": 6000,
    "image": "https://upload.wikimedia.org/wikipedia/commons/1/15/Blueberries.jpg"
  },
  {
    "id": 4,
    "name": "딸기와플",
    "price": 7000,
    "image": "https://upload.wikimedia.org/wikipedia/commons/2/29/PerfectStrawberry.jpg"
  }]);
  const [selectMenu, setSelect] = useState("");

  const select = (idx) => {
    setSelect(idx)
    console.log(document.getElementById(idx.id))
  }

  const Menu = menu_list.map((list) => (
    <div id={list.id} className={"Menu" + (list.id === selectMenu.id ? "select" : "")} onClick={() => { select(list) }}>
      <span id='selectid' className='MenuID'>{list.id}</span>
      <span className='MenuName'>{list.name}</span>
      <span className='MenuPrice'>{list.price.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")}</span>
    </div>
  ))

  const [AddmodalOpen, setAddmodalOpen] = useState(false);
  const showAddmodal = () => {
    setAddmodalOpen(true);
  };

  return (
    <div className="MenuBox">
      <div className='MenuBox_1'>
        <div className='Search'>
          <span className='Searchst'> 이름 검색 :</span><input id="SearchInput"></input>
        </div>
        <div className='MenuListBox'>
          <div className='MenuContent'>
            <div className='ID'><b>ID</b></div>
            <div className='Name'><b>이름</b></div>
            <div className='Price'><b>가격</b></div>
          </div>
          {Menu}
          <button id='AddButton' onClick={showAddmodal}>+</button>
        </div>
      </div>

      {selectMenu !== "" && <div className='MenuBox_2'>
        <button onClick={() => { setSelect("") }}>x</button>
        <image className='selectimage' src={selectMenu.image}></image>
        <p>{selectMenu.name}</p>
        <p>{selectMenu.price.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")}원</p>
        <div>
          <button>수정</button>
          <button>삭제</button>
        </div>

      </div>}
      {AddmodalOpen && <Addmodal setAddmodalOpen={setAddmodalOpen} menu_list={menu_list} setMenu={setMenu} maxId={maxId} setMaxId={setMaxId} />}
    </div>

  );
}

export default MenuBox;
