import { useState } from 'react';
import '../css/MenuBox.css';
import '../css/Addmodal.css';
import './Addmodal.js';



function MenuBox() {

  const [menu_list, setMenu] = useState([ {
    "id": 1,
    "name": "초코와플",
    "price": 7000,
    "image": ""
  },
  {
    "id": 2,
    "name": "아메리카노",
    "price": 4000,
    "image": ""
  },
  {
    "id": 3,
    "name": "블루베리스무디",
    "price": 6000,
    "image": "https://upload.wikimedia.org/wikipedia/commons/1/15/Blueberries.jpg"
  },
  {
    "id": 4,
    "name": "딸기와플",
    "price": 7000,
    "image": "https://upload.wikimedia.org/wikipedia/commons/2/29/PerfectStrawberry.jpg"
  }]);
 
  const Menu = menu_list.map((list) => (
    <div>
    <div className='Menu'>
      <span className='MenuID'>{list.id}</span>
      <span className='MenuName'>{list.name}</span>
      <span className='MenuPrice'>{list.price}</span>
    </div>
    <hr></hr>
    </div>
  ))
  
  const Addmodal = document.getElementById("Addmodal")

  return (
    <div className="MenuBox">
      <div className='MenuBox_1'>
        <div className='Search'>
          <span> 이름 검색 :</span><input></input>
        </div>
        <div className='MenuListBox'>
          <div>
          <div className='Menu'>
            <div className='MenuID'><b>ID</b></div>
            <div className='MenuName'><b>이름</b></div>
            <div className='MenuPrice'><b>가격</b></div>
          </div>
          <hr></hr>
          </div>
          {Menu}
          <button className='AddButton'>+</button>
        </div>
      </div>
      <div className='MenuBox_2'>
        <p>{menu_list[0].id}</p>
        <p>{menu_list[0].price}</p>
      </div>

    </div>
    
  );
}

export default MenuBox;
