import '../css/Addmodal.css';
import React from "react";
import { useRef, useEffect } from 'react';

function Addmodal({ setAddmodalOpen, setMenu, menu_list, maxId, setMaxId }) {


  const closeAddmodal = () => {
    setAddmodalOpen(false);
  }

  const addMenu = () => {
    const name = document.getElementById('name').value
    const price = document.getElementById('price').value
    const image = document.getElementById('image').value
    const new_menu_list = [...menu_list, {id: maxId, name: name, price: price, image: image}]
    setMenu(new_menu_list);
    setMaxId(maxId+1);
  }

  const AddmodalRef = useRef();
  useEffect(() => {
    const handler = (e) => {
      if (AddmodalRef.current && !AddmodalRef.current.contains(e.target)) {
        setAddmodalOpen(false);
      }
    };

    document.addEventListener('mousedown', handler);

    return () => {
      document.removeEventListener('mousedown', handler);
    };
  });


  return (
    <div className='background'>
      <div ref={AddmodalRef} className='container'>
        <b className='title'>메뉴 추가</b>
        <p className='line'> <b className='subtitle'>이름</b><input id='name' type='text' className='input'></input></p>
        <p className='line'> <b className='subtitle'>가격</b><input id='price' type='text' className='input'></input></p>
        <p className='line'> <b className='subtitle'>상품 이미지</b><input id='image' type='text' className='input'></input></p>
        <div className='buttonArea'>
        <button className='add'onClick={addMenu}>
          추가
        </button>
        <button className='close' onClick={closeAddmodal}>
          취소
        </button>
        </div>
      </div>
    </div>
  );
}

export default Addmodal;
