import '../css/Addmodal.css';
import React from "react";
import { useRef, useEffect } from 'react';

function Addmodal({ setAddmodalOpen }) {


  const closeAddmodal = () => {
    setAddmodalOpen(false);
  }

  const AddmodalRef = useRef();
  useEffect(() => {
    const handler = (e) => {
      if (AddmodalRef.current && !AddmodalRef.current.contains(e.target)) {
        setAddmodalOpen(false);
      }
    };

    document.addEventListener('mousedown', handler);

    return () => {
      document.removeEventListener('mousedown', handler);
    };
  });


  return (
    <div className='background'>
      <div ref={AddmodalRef} className='container'>
        <b className='title'>메뉴 추가</b>
        <p className='line'> <span className='subtitle'>이름 :</span><input className='input'></input></p>
        <p className='line'> <span className='subtitle'>가격 :</span><input className='input'></input></p>
        <p className='line'> <span className='subtitle'>상품 이미지 :</span><input className='input'></input></p>
        <button className='close' onClick={closeAddmodal}>
          취소
        </button>
      </div>
    </div>
  );
}

export default Addmodal;
